mapper.insert:

            SELECT SEQ_CHAT_ROOM.CURRVAL FROM DUAL

		INSERT
		  INTO
		       TB_CHAT_ROOM
			   (
			   ROOM_NO
			 , STATUS
			 , CREATED_DATE
			 , ESTIMATE_NO
			   )
		VALUES (
			   SEQ_CHAT_ROOM.NEXTVAL
			, #{status}
			, #{createDate}
			, #{estimateNo}
			  )

mapper.select:
SELECT m.MESSAGE_NO , m.USER_NO , m.CONTENT , m.SENT_DATE , m.STATUS , m.ROOM_NO , m.TYPE , u.NICKNAME , u.USER_ROLE , CASE WHEN EXISTS(SELECT 1 FROM TB_REPORT r JOIN TB_CHAT_ROOM cr ON r.ESTIMATE_NO = cr.ESTIMATE_NO WHERE cr.ROOM_NO = m.ROOM_NO) THEN 1 ELSE 0 END AS REPORTED , CASE WHEN EXISTS(SELECT 1 FROM TB_REVIEW rv JOIN TB_CHAT_ROOM cr ON rv.ESTIMATE_NO = cr.ESTIMATE_NO WHERE cr.ROOM_NO = m.ROOM_NO) THEN 1 ELSE 0 END AS REVIEWED , CASE WHEN EXISTS(SELECT 1 FROM TB_PAYMENT p WHERE p.ROOM_NO = m.ROOM_NO) THEN 1 ELSE 0 END AS PAID FROM TB_CHAT_ROOM_MESSAGE m JOIN TB_MEMBER u ON m.USER_NO = u.USER_NO WHERE ROOM_NO = #{roomNo}
AND MESSAGE_NO < #{messageNo}
ORDER BY MESSAGE_NO DESC FETCH FIRST #{size} ROWS ONLY

mapper.insert:
INSERT
  INTO
       TB_CHAT_ROOM_USER
	   (
	   USER_NO
	 , ROOM_NO
	   )
VALUES (
	   #{userNo}
	 , #{roomNo}
	   )


	SELECT SEQ_CHAT_ROOM_MESSAGE.CURRVAL FROM DUAL

INSERT
  INTO
       TB_CHAT_ROOM_MESSAGE
	   (
	   MESSAGE_NO
	 , USER_NO
	 , CONTENT
	 , SENT_DATE
	 , STATUS
	 , ROOM_NO
	 , TYPE
	   )
VALUES (
	   SEQ_CHAT_ROOM_MESSAGE.NEXTVAL
	, #{userNo}
	, #{content}
	, #{sentDate}
	, #{status}
	, #{roomNo}
	, #{type}
	  )


	SELECT SEQ_CHAT_ROOM_MESSAGE_ATTACHMENT.NEXTVAL FROM DUAL

INSERT
  INTO
       TB_CHAT_ROOM_MESSAGE_ATTACHMENT
	   (
