	 , MESSAGE_NO
	 , FILE_PATH
	 , ORIGIN_NAME
	 , UPLOAD_DATE
	 , STATUS
	   )
VALUES 
      (
	  SEQ_MESSAGE_ATTACHMENT.NEXTVAL
