mapper.insert:

		    SELECT SEQ_REVIEW.NEXTVAL FROM DUAL

       INSERT 
		 INTO 
		      TB_REVIEW 
			  (
              REVIEW_NO
            , ESTIMATE_NO
            , CONTENT
            , STAR_SCORE
            , STATUS
            , CREATE_DATE
              )
			  VALUES 
			  (
              #{reviewNo}
            , #{estimateNo}
            , #{content}
            , #{starScore}
            , #{status}
            , SYSDATE
              )


            SELECT SEQ_REVIEW_ATTACHMENT.NEXTVAL FROM DUAL

       INSERT 
		 INTO 
		      TB_REVIEW_ATTACHMENT 
			  (
              FILE_NO
            , REVIEW_NO
            , ORIGIN_NAME
            , FILE_PATH
            , STATUS
            , UPLOAD_DATE
              ) 
			  VALUES 
			  (
              #{fileNo}
            , #{reviewNo}
            , #{originName}
            , #{filePath}
            , #{status}
            , SYSDATE
              )

       INSERT 
		 INTO 
		      TB_REVIEW_MAP 
			  (
              REVIEW_NO
            , TAG_NO
              ) 
			  VALUES 
			  (
              #{reviewNo}
            , #{tagNo}
              )
